	// custom fields globally
	$all_fields = get_option( 'um_fields', array() );
	foreach ( $all_fields as &$field ) {
		if ( array_key_exists( 'icon', $field ) ) {
			if ( array_key_exists( $field['icon'], $icons_assoc ) ) {
				$field['icon'] = $icons_assoc[ $field['icon'] ];
			}
		}
	}
	update_option( 'um_fields', $all_fields );

	// UM Forms custom fields
	global $wpdb;
	$results = $wpdb->get_results( "SELECT * FROM {$wpdb->postmeta} WHERE meta_key = '_um_custom_fields'", ARRAY_A );
	foreach ( $results as $row ) {
		$meta_value = maybe_unserialize( $row['meta_value'] );
		if ( array_key_exists( 'icon', $meta_value ) ) {
			if ( array_key_exists( $meta_value['icon'], $icons_assoc ) ) {
				$meta_value['icon'] = $icons_assoc[ $meta_value['icon'] ];
				update_post_meta( $row['post_id'], '_um_custom_fields', $meta_value );
			}
		}
	}

	// Profile Tabs
	$results = $wpdb->get_results( "SELECT * FROM {$wpdb->postmeta} WHERE meta_key = 'um_icon'", ARRAY_A );
	foreach ( $results as $row ) {
		$meta_value = $row['meta_value'];
		if ( array_key_exists( $meta_value, $icons_assoc ) ) {
			$meta_value = $icons_assoc[ $meta_value ];
			update_post_meta( $row['post_id'], 'um_icon', $meta_value );
		}
	}

	// Notices
	$results = $wpdb->get_results( "SELECT * FROM {$wpdb->postmeta} WHERE meta_key = '_um_icon'", ARRAY_A );
	foreach ( $results as $row ) {
		$meta_value = $row['meta_value'];
		if ( array_key_exists( $meta_value, $icons_assoc ) ) {
			$meta_value = $icons_assoc[ $meta_value ];
			update_post_meta( $row['post_id'], '_um_icon', $meta_value );
		}
	}

	// Private Content
	$private_content_icon = UM()->options()->get( 'tab_private_content_icon' );
	if ( ! empty( $private_content_icon ) ) {
		if ( array_key_exists( $private_content_icon, $icons_assoc ) ) {
			$private_content_icon = $icons_assoc[ $private_content_icon ];
			UM()->options()->update( 'tab_private_content_icon', $private_content_icon );
