	$um_user_bookmarks_bookmarked_icon = UM()->options()->get( 'um_user_bookmarks_bookmarked_icon' );
	if ( ! empty( $um_user_bookmarks_bookmarked_icon ) ) {
		if ( array_key_exists( $um_user_bookmarks_bookmarked_icon, $icons_assoc ) ) {
