			UM()->options()->update( 'um_user_bookmarks_regular_icon', $um_user_bookmarks_regular_icon );
		}
	}

	wp_send_json_success( array( 'message' => __( 'Fonticons have been updated successfully.', 'ultimate-member' ) ) );
}


function um_upgrade_options30() {
	UM()->ajax()->check_nonce( 'um-admin-nonce' );

	um_maybe_unset_time_limit();

	$time = UM()->options()->get( 'activation_link_expiry_time' );
	if ( ! empty( $time ) ) {
		$time = ceil( $time / DAY_IN_SECONDS );
		UM()->options()->update( 'activation_link_expiry_time', $time );
	}

	// set v3 design disabled for old customers
	UM()->options()->update( 'enable_version_3_design', false );
	update_option( 'um_is_legacy', true );
	update_option( 'um_last_version_upgrade', '3.0' );

	wp_send_json_success( array( 'message' => __( 'Settings have been updated successfully.', 'ultimate-member' ) ) );
}
